from Bio.Seq import Seq

#This is the example on Rosalind 'Intro to the bioinformatics armory

def CountBases(x):
    my_seq = Seq(x)
    A=my_seq.count("A")
    T=my_seq.count("T")
    G=my_seq.count("G")
    C=my_seq.count("C")
    print(A, C, G, T)


x = 'AGCTTTTCATTCTGACTGCAACGGGCAATATGTCTCTGTGTGGATTAAAAAAAGAGTGTCTGATAGCAGC'
CountBases(x)

